enum ActionKind {
    Walking,
    Idle,
    Jumping
}
namespace SpriteKind {
    export const Basketball = SpriteKind.create()
    export const powerup = SpriteKind.create()
}
scene.onHitTile(SpriteKind.Basketball, 11, function (sprite) {
    info.changeLifeBy(-1)
    scene.placeOnRandomTile(ball, 9)
    music.jumpDown.play()
})
scene.onHitTile(SpriteKind.Basketball, 10, function (sprite) {
    if (level == 5) {
        game.over(true)
    } else if (level == 4) {
        music.powerUp.play()
        level += 1
        setup_level()
        power_up.destroy()
    } else if (level == 3) {
        music.powerUp.play()
        level += 1
        setup_level()
    } else if (level == 2) {
        music.powerUp.play()
        level += 1
        setup_level()
    } else if (level == 1) {
        music.powerUp.play()
        level += 1
        setup_level()
    }
})
function create_bounce_animation () {
    if (ball.isHittingTile(CollisionDirection.Bottom)) {
        ball.vy += -200
    }
}
function transfer_powermap () {
    clear_level()
    setup_level()
    coin.destroy()
    level = 0
    info.startCountdown(10)
    scene.centerCameraAt(8, 7)
    ball.setStayInScreen(true)
    ball.ay = 0
    controller.moveSprite(ball)
    pause(10000)
    destroy_ball()
    if (level == 1) {
        level = 5
        setup_level()
        create_ball()
        create_bounce_animation()
        create_rolling_animation()
        create_map()
    }
}
