controller.left.onEvent(ControllerButtonEvent.Pressed, function () {
    animation.setAction(ball, ActionKind.Walking)
})
sprites.onOverlap(SpriteKind.Basketball, SpriteKind.powerup, function (sprite, otherSprite) {
    level = 0
    otherSprite.destroy(effects.spray, 50)
    music.beamUp.play()
    clear_forPowerUp()
    transfer_powermap()
})
controller.right.onEvent(ControllerButtonEvent.Released, function () {
    animation.setAction(ball, ActionKind.Idle)
})
controller.left.onEvent(ControllerButtonEvent.Released, function () {
    animation.setAction(ball, ActionKind.Idle)
})
info.onCountdownEnd(function () {
    level = 1
})
scene.onHitTile(SpriteKind.Basketball, 8, function (sprite) {
    game.over(false)
    music.wawawawaa.play()
})
controller.right.onEvent(ControllerButtonEvent.Pressed, function () {
    animation.setAction(ball, ActionKind.Walking)
})
sprites.onOverlap(SpriteKind.Basketball, SpriteKind.Food, function (sprite, otherSprite) {
    info.changeScoreBy(1)
    otherSprite.destroy()
    music.baDing.play()
})
function setup_level () {
    clear_level()
    game.splash("Level " + level)
    create_map()
    create_coins()
    create_powerups()
    scene.placeOnRandomTile(ball, 9)
}
scene.onHitTile(SpriteKind.Basketball, 2, function (sprite) {
    setup_level()
})
